import React from 'react';
import allProjects from '../src/portfolio.json';
import Projects from './components/Projects';
import "./App.css";
import Header from './components/Header'
import Navbar from './components/Navbar'
import Footer from './components/Footer'
import AboutMe from "./components/AboutMe"

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      projects: allProjects, 
      projectsToDisplay: [],
      dimensions: null
     };
  }

  componentDidMount() {
    this.setState({ projectsToDisplay: allProjects });
    this.setState({
      dimensions: {
        width: this.container.offsetWidth,
        height: this.container.offsetHeight,
      },
    })
  }

  renderProjects = () => {
    return this.state.projectsToDisplay.map((project, index) => {
      const { imgURL, Title, Desc, URL, Code } = project;
      return (
        <Projects
          key={index}
          imgURL={imgURL}
          title={Title}
          desc={Desc}
          url={URL}
          code={Code}
        ></Projects>
      );
    });
  };


  render() {
    const { dimensions } = this.state
    return (
      <div>
        <Header></Header>
        <Navbar />
        <AboutMe></AboutMe>
        <div className="projects">
          <div id="project-container">{this.renderProjects()}</div>
        </div>
        <Footer></Footer>
      </div>
    );
  }
}

export default App;
